import React, { useState, useEffect } from 'react';
import { Resizable } from 'react-resizable';
import axios from 'axios';

import './App.css'; // Import CSS file

const Layout = () => {
  const [sizes, setSizes] = useState([300, 300, 400]);
  const [addCount, setAddCount] = useState(0);
  const [updateCount, setUpdateCount] = useState(0);

  useEffect(() => {
    // Fetch initial count values when component mounts
    fetchCount();
  }, []);

  const handleResize = (index, { size }) => {
    const newSizes = [...sizes];
    newSizes[index] = size.width;
    setSizes(newSizes);
  };



  const handleUpdate = () => {
    axios.post('http://localhost:3001/api/data', {
      action: 'Update',
      newData: {
        component1: 'Updated data for Component 1',
        component2: 'Updated data for Component 2',
        component3: 'Updated data for Component 3'
      }
    })
    .then(response => {
      console.log(response.data);
      fetchCount(); // Update count after successful update
    })
    .catch(error => {
      console.error('Error:', error);
    });
  };

  const fetchCount = () => {
    axios.get('http://localhost:3001/api/count')
    .then(response => {
      const { addCount, updateCount } = response.data;
      setAddCount(addCount);
      setUpdateCount(updateCount);
    })
    .catch(error => {
      console.error('Error:', error);
    });
  };

  return (
    <div className="container">
      <div className="flex-container">
        <Resizable
          width={sizes[0]}
          height={Infinity}
          onResize={(e, data) => handleResize(0, data)}
        >
          <div className="resizable-component">
            Component 1
          </div>
        </Resizable>
        <Resizable
          width={sizes[1]}
          height={Infinity}
          onResize={(e, data) => handleResize(1, data)}
        >
          <div className="resizable-component">
            Component 2
          </div>
        </Resizable>
      </div>
      <Resizable
        width={sizes[2]}
        height={Infinity}
        onResize={(e, data) => handleResize(2, data)}
      >
        <div className="resizable-component">
          Component 3
        </div>
      </Resizable>
      <div className="button-container">
        <button onClick={handleAdd}>Add Data</button>
        <button onClick={handleUpdate}>Update Data</button>
        <p>Add Count: {addCount}</p>
        <p>Update Count: {updateCount}</p>
      </div>
    </div>
  );
};

export default Layout;
