import React, { useState } from 'react';
import { Resizable } from 'react-resizable';

import './App.css'; // Import CSS file

const Layout = () => {
  const [sizes, setSizes] = useState([300, 300, 400]);
  const [operationCount, setOperationCount] = useState(0); // New state for operation count
  const [inputValues, setInputValues] = useState({
    component1: '',
    component2: '',
    component3: ''
  });

  const handleResize = (index, { size }) => {
    const newSizes = [...sizes];
    newSizes[index] = size.width;
    setSizes(newSizes);
  };

  const handleInputChange = (event, componentName) => {
    const { value } = event.target;
    setInputValues(prevState => ({
      ...prevState,
      [componentName]: value
    }));
  };

  const handleAddOrUpdate = (operation) => { // Combine add and update handlers
    // Your logic for adding or updating data
    console.log('Operation:', operation);
    console.log('Data:', inputValues);
    setInputValues({
      component1: '',
      component2: '',
      component3: ''
    });
    setOperationCount(operationCount + 1); // Update operation count locally
  };

  return (
    <div className="container">
      <div className="flex-container">
        <Resizable
          width={sizes[0]}
          height={Infinity}
          onResize={(e, data) => handleResize(0, data)}
        >
          <div className="resizable-component">
            <h1>Component 1</h1>
            <input
              type="text"
              value={inputValues.component1}
              onChange={(e) => handleInputChange(e, 'component1')}
              style={{ width: '100%', height: '100%', border: 'none', outline: 'none' }}
            />
          </div>
        </Resizable>
        <Resizable
          width={sizes[1]}
          height={Infinity}
          onResize={(e, data) => handleResize(1, data)}
        >
          <div className="resizable-component">
            <h1>Component 2</h1>
            <input
              type="text"
              value={inputValues.component2}
              onChange={(e) => handleInputChange(e, 'component2')}
              style={{ width: '100%', height: '100%', border: 'none', outline: 'none' }}
            />
          </div>
        </Resizable>
      </div>
      <Resizable
        width={sizes[2]}
        height={Infinity}
        onResize={(e, data) => handleResize(2, data)}
      >
        <div className="resizable-component">
          <h1>Component 3</h1>
          <input
            type="text"
            value={inputValues.component3}
            onChange={(e) => handleInputChange(e, 'component3')}
            style={{ width: '100%', height: '100%', border: 'none', outline: 'none' }}
          />
        </div>
      </Resizable>
      <div className="button-container">
        <button onClick={() => handleAddOrUpdate('add')}>Add Data</button>
        <button onClick={() => handleAddOrUpdate('update')}>Update Data</button>
        <p>Count: {operationCount}</p> {/* Displaying the total count */}
      </div>
    </div>
  );
};

export default Layout;
