import React, { useState } from 'react';
import { Resizable } from 'react-resizable';
import './App.css';

const App = () => {
  const [width1, setWidth1] = useState(300);
  const [width2, setWidth2] = useState(300);
  const [width3, setWidth3] = useState(300);

  const onResize1 = (event, { size }) => {
    setWidth1(size.width);
  };

  const onResize2 = (event, { size }) => {
    setWidth2(size.width);
  };

  const onResize3 = (event, { size }) => {
    setWidth3(size.width);
  };

  return (
    <div className="container">
      <Resizable className="component" width={width1} height={200} onResize={onResize1}>
        <div>Component 1</div>
      </Resizable>
      <Resizable className="component" width={width2} height={200} onResize={onResize2}>
        <div>Component 2</div>
      </Resizable>
      <Resizable className="component" width={width3} height={200} onResize={onResize3}>
        <div>Component 3</div>
      </Resizable>
    </div>
  );
};

export default App;
