import React, { useState } from 'react';
import { Resizable } from 'react-resizable';

const Layout = () => {
  const [sizes, setSizes] = useState([200, 300, 400]);

  const handleResize = (index, { size }) => {
    const newSizes = [...sizes];
    newSizes[index] = size.width;
    setSizes(newSizes);
  };

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      <Resizable
        width={sizes[0]}
        height={Infinity}
        onResize={(e, data) => handleResize(0, data)}
      >
        <div style={{ border: '1px solid black', margin: '5px', padding: '10px' }}>
          Component 1
        </div>
      </Resizable>
      <Resizable
        width={sizes[1]}
        height={Infinity}
        onResize={(e, data) => handleResize(1, data)}
      >
        <div style={{ border: '1px solid black', margin: '5px', padding: '10px' }}>
          Component 2
        </div>
      </Resizable>
      <Resizable
        width={sizes[2]}
        height={Infinity}
        onResize={(e, data) => handleResize(2, data)}
      >
        <div style={{ border: '1px solid black', margin: '5px', padding: '10px' }}>
          Component 3
        </div>
      </Resizable>
    </div>
  );
};

export default Layout;
