import React, { useState } from 'react';
import { Resizable } from 'react-resizable';

const Layout = () => {
  const [sizes, setSizes] = useState([300, 300, 400]);

  const handleResize = (index, { size }) => {
    const newSizes = [...sizes];
    newSizes[index] = size.width;
    setSizes(newSizes);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', height: '100vh' }}>
      <div style={{ display: 'flex', flex: '1 1 auto' }}>
        <Resizable
          width={sizes[0]}
          height={Infinity}
          onResize={(e, data) => handleResize(0, data)}
        >
          <div style={{ border: '1px solid black', margin: '5px', padding: '10px', flex: '1 0 auto' }}>
            Component 1
          </div>
        </Resizable>
        <Resizable
          width={sizes[1]}
          height={Infinity}
          onResize={(e, data) => handleResize(1, data)}
        >
          <div style={{ border: '1px solid black', margin: '5px', padding: '10px', flex: '1 0 auto' }}>
            Component 2
          </div>
        </Resizable>
      </div>
      <div style={{ display: 'flex', flex: 'none' }}>
        <Resizable
          width={sizes[2]}
          height={Infinity}
          onResize={(e, data) => handleResize(2, data)}
        >
          <div style={{ border: '1px solid black', margin: '5px', padding: '10px', width: '100%' }}>
            Component 3
          </div>
        </Resizable>
      </div>
    </div>
  );
};

export default Layout;
