import React, { useState, useEffect } from 'react';
import { Resizable } from 'react-resizable';
import axios from 'axios';

import './App.css'; // Import CSS file

const Layout = () => {
  const [sizes, setSizes] = useState([300, 300, 400]);
  const [addCounts, setAddCounts] = useState([0, 0, 0]);
  const [updateCounts, setUpdateCounts] = useState([0, 0, 0]);

  useEffect(() => {
    // Fetch initial count values when component mounts
    fetchCounts();
  }, []);

  const handleResize = (index, { size }) => {
    const newSizes = [...sizes];
    newSizes[index] = size.width;
    setSizes(newSizes);
  };

  const handleAdd = (index) => {
    axios.post('http://localhost:3001/api/data', {
      action: 'Add',
      newData: {
        [`component${index + 1}`]: `New data for Component ${index + 1}`
      }
    })
    .then(response => {
      console.log(response.data);
      fetchCounts(); // Update count after successful add
    })
    .catch(error => {
      console.error('Error:', error);
    });
  };

  const handleUpdate = (index) => {
    axios.post('http://localhost:3001/api/data', {
      action: 'Update',
      newData: {
        [`component${index + 1}`]: `Updated data for Component ${index + 1}`
      }
    })
    .then(response => {
      console.log(response.data);
      fetchCounts(); // Update count after successful update
    })
    .catch(error => {
      console.error('Error:', error);
    });
  };

  const fetchCounts = () => {
    axios.get('http://localhost:3001/api/count')
    .then(response => {
      const { addCounts, updateCounts } = response.data;
      setAddCounts(addCounts);
      setUpdateCounts(updateCounts);
    })
    .catch(error => {
      console.error('Error:', error);
    });
  };

  return (
    <div className="container">
      <div className="flex-container">
        {[0, 1, 2].map(index => (
          <Resizable
            key={index}
            width={sizes[index]}
            height={Infinity}
            onResize={(e, data) => handleResize(index, data)}
          >
            <div className="resizable-component">
              <input type="text" placeholder={`Data for Component ${index + 1}`} />
              <button onClick={() => handleAdd(index)}>Add</button>
              <button onClick={() => handleUpdate(index)}>Update</button>
              <p>Add Count: {addCounts[index]}</p>
              <p>Update Count: {updateCounts[index]}</p>
            </div>
          </Resizable>
        ))}
      </div>
      <div className="count-container">
        <p>Total Add Count: {addCounts.reduce((acc, curr) => acc + curr, 0)}</p>
        <p>Total Update Count: {updateCounts.reduce((acc, curr) => acc + curr, 0)}</p>
      </div>
    </div>
  );
};

export default Layout;
