import React, { useState } from 'react';
import { Resizable } from 'react-resizable';
import axios from 'axios';

import './App.css'; // Import CSS file

const Layout = () => {
  const [sizes, setSizes] = useState([300, 300, 400]);
  const [totalCount, setTotalCount] = useState(0); // New state for total count
  const [inputValues, setInputValues] = useState({
    component1: '',
    component2: '',
    component3: ''
  });

  const handleResize = (index, { size }) => {
    const newSizes = [...sizes];
    newSizes[index] = size.width;
    setSizes(newSizes);
  };

  const handleInputChange = (event, componentName) => {
    const { value } = event.target;
    setInputValues(prevState => ({
      ...prevState,
      [componentName]: value
    }));
  };

  const handleAddData = () => {
  const inputData = { ...inputValues }; // Save the current input values
  axios.post('http://localhost:3001/api/add', inputValues)
    .then(response => {
      const { totalCount } = response.data;
      setTotalCount(totalCount);
      // Clear the input fields only if they are empty
      if (Object.values(inputValues).every(value => value === '')) {
        setInputValues({
          component1: '',
          component2: '',
          component3: ''
        });
      }
      // Restore the saved input values after the request is successful
      setInputValues(inputData);
    })
    .catch(error => {
      console.error('Error:', error);
    });
};



  const handleUpdateData = () => {
    axios.post('http://localhost:3001/api/update', inputValues)
      .then(response => {
        const { totalCount } = response.data;
        setTotalCount(totalCount);
        setInputValues({
          component1: '',
          component2: '',
          component3: ''
        });
      })
      .catch(error => {
        console.error('Error:', error);
      });
  };

  return (
    <div className="container">
      <div className="flex-container">
        <Resizable
          width={sizes[0]}
          height={Infinity}
          onResize={(e, data) => handleResize(0, data)}
        >
          <div className="resizable-component">
            <h1>Component 1</h1>
            <div className="input-container">
              <input
                type="text"
                value={inputValues.component1}
                onChange={(e) => handleInputChange(e, 'component1')}
              />
            </div>
            <div class="button-container">
            <button class='button1' onClick={() => handleAddData(0)}>Add</button>
            <button class='button1' onClick={() => handleUpdateData(0)}>Update</button>
            </div>
          </div>
        </Resizable>
        <Resizable
          width={sizes[1]}
          height={Infinity}
          onResize={(e, data) => handleResize(1, data)}
        >
          <div className="resizable-component">
            <h1>Component 2</h1>
            <div className="input-container">
              <input
                type="text"
                value={inputValues.component2}
                onChange={(e) => handleInputChange(e, 'component2')}
              />
            </div>
            <div class="button-container">
            <button class='button1' onClick={() => handleAddData(1)}>Add</button>
            <button class='button1' onClick={() => handleUpdateData(1)}>Update</button>
            </div>
          </div>
        </Resizable>
      </div>
      <Resizable
        width={sizes[2]}
        height={Infinity}
        onResize={(e, data) => handleResize(2, data)}
      >
        <div className="resizable-component">
          <h1>Component 3</h1>
          <div className="input-container">
            <input
              type="text"
              value={inputValues.component3}
              onChange={(e) => handleInputChange(e, 'component3')}
            />
          </div>
          <div class="button-container">
            <button class='button1' onClick={() => handleAddData(2)}>Add</button>
            <button class='button1' onClick={() => handleUpdateData(2)}>Update</button>
            </div>
        </div>
      </Resizable>
      <div className="button-container">
        <p>Total Count: {totalCount}</p>
      </div>
    </div>
  );
};

export default Layout;
