import React, { useState } from 'react';
import axios from 'axios';
import { Resizable } from 'react-resizable';
import './App.css';

function App() {
  const [componentWidths, setComponentWidths] = useState({
    component1: 300,
    component2: 300,
    component3: 600, // Set the initial width of component3
  });

  const handleResize = (component, { size }) => {
    setComponentWidths(prevWidths => {
      const remainingComponents = Object.keys(prevWidths).filter(key => key !== component);
      const totalRemainingWidth = remainingComponents.reduce((acc, key) => acc + prevWidths[key], 0);
      const newWidth = size.width;
      const newWidthForEach = totalRemainingWidth > 0 ? totalRemainingWidth / remainingComponents.length : 0;

      const updatedWidths = {
        ...prevWidths,
        [component]: newWidth,
      };

      remainingComponents.forEach(key => {
        updatedWidths[key] = newWidthForEach;
      });

      return updatedWidths;
    });
  };


  const handleAdd = () => {
    axios.post('/api/add')
      .then(response => {
        console.log(response.data.message);
        // Add logic to clear data in components
      })
      .catch(error => {
        console.error('Error adding data:', error);
      });
  };

  const handleUpdate = () => {
    axios.post('/api/update')
      .then(response => {
        console.log(response.data.message);
        // Add logic to update data in components
      })
      .catch(error => {
        console.error('Error updating data:', error);
      });
  };

  const getCount = () => {
    axios.get('/api/count')
      .then(response => {
        console.log('Add count:', response.data.addCount);
        console.log('Update count:', response.data.updateCount);
      })
      .catch(error => {
        console.error('Error fetching count:', error);
      });
  };

  return (
    <div className="layout">
      <div className="upper-layout">
        <Resizable
          className="full-width-component"
          width={componentWidths.component1}
          height={200}
          onResize={(e, data) => handleResize('component1', data)}
        >
          <div>
            Component 1
            {/* Add your content here */}
          </div>
        </Resizable>
        <Resizable
          className="full-width-component"
          width={componentWidths.component2}
          height={200}
          onResize={(e, data) => handleResize('component2', data)}
        >
          <div>
            Component 2
            {/* Add your content here */}
          </div>
        </Resizable>
      </div>
      <Resizable
        className="full-width-component"
        width={componentWidths.component3}
        height={200}
        onResize={(e, data) => handleResize('component3', data)}
      >
        <div>
          Component 3
          {/* Add your content here */}
        </div>
      </Resizable>
      <div className="controls">
        <button onClick={handleAdd}>Add</button>
        <button onClick={handleUpdate}>Update</button>
        <button onClick={getCount}>Count</button>
      </div>
    </div>
  );
}

export default App;
