import React from 'react';
import { Resizable } from 'react-resizable';
import './App.css'; // You can define your CSS for layout styling

class App extends React.Component {
  state = {
    componentWidths: [300, 300, 300] // Initial widths of components
  };

  onResize = (index, event, { size }) => {
    const { componentWidths } = this.state;
    const newWidths = [...componentWidths];
    newWidths[index] = size.width;
    this.setState({ componentWidths: newWidths });
  };

  render() {
    const { componentWidths } = this.state;

    return (
      <div className="layout">
        <ResizableBox
          width={componentWidths[0]}
          height={200}
          minConstraints={[200, 200]}
          onResize={(event, size) => this.onResize(0, event, size)}
        >
          <div className="component">Component 1</div>
        </ResizableBox>
        <ResizableBox
          width={componentWidths[1]}
          height={200}
          minConstraints={[200, 200]}
          onResize={(event, size) => this.onResize(1, event, size)}
        >
          <div className="component">Component 2</div>
        </ResizableBox>
        <ResizableBox
          width={componentWidths[2]}
          height={200}
          minConstraints={[200, 200]}
          onResize={(event, size) => this.onResize(2, event, size)}
        >
          <div className="component">Component 3</div>
        </ResizableBox>
      </div>
    );
  }
}

export default App;
