import React, { useState, useEffect } from 'react';
import { Resizable } from 'react-resizable';
import axios from 'axios';

import './App.css'; // Import CSS file

const Layout = () => {
  const [sizes, setSizes] = useState([300, 300, 400]);
  const [addCounts, setAddCounts] = useState([0, 0, 0]);
  const [updateCounts, setUpdateCounts] = useState([0, 0, 0]);

  useEffect(() => {
    // Fetch initial counts when component mounts
    fetchCounts();
  }, []);

  const handleResize = (index, { size }) => {
    const newSizes = [...sizes];
    newSizes[index] = size.width;
    setSizes(newSizes);
  };

  const handleAdd = (index) => {
    axios.post('http://localhost:3001/api/data', {
      action: 'Add',
      componentIndex: index, // Add the index of the component
      newData: `New data for Component ${index + 1}` // Example data for demonstration
    })
    .then(response => {
      console.log(response.data);
      fetchCounts(); // Update counts after successful add
    })
    .catch(error => {
      console.error('Error:', error);
    });
  };

  const handleUpdate = (index) => {
    axios.post('http://localhost:3001/api/data', {
      action: 'Update',
      componentIndex: index, // Add the index of the component
      newData: `Updated data for Component ${index + 1}` // Example data for demonstration
    })
    .then(response => {
      console.log(response.data);
      fetchCounts(); // Update counts after successful update
    })
    .catch(error => {
      console.error('Error:', error);
    });
  };

  const fetchCounts = () => {
    axios.get('http://localhost:3001/api/count')
    .then(response => {
      const { addCounts, updateCounts } = response.data;
      setAddCounts(addCounts);
      setUpdateCounts(updateCounts);
    })
    .catch(error => {
      console.error('Error:', error);
    });
  };

  return (
    <div className="container">
      <div className="flex-container">
        {sizes.map((size, index) => (
          <Resizable
            key={index}
            width={size}
            height={Infinity}
            onResize={(e, data) => handleResize(index, data)}
          >
            <div className="resizable-component">
              Component {index + 1}
              <button onClick={() => handleAdd(index)}>Add Data</button>
              <button onClick={() => handleUpdate(index)}>Update Data</button>
              <p>Add Count: {addCounts[index]}</p>
              <p>Update Count: {updateCounts[index]}</p>
            </div>
          </Resizable>
        ))}
      </div>
    </div>
  );
};

export default Layout;
